}

class Main extends Component{
    render(){ 
        return <div>
            <Title title = {'toDos'}/>
            <List tasks = {["hose the driveway", "finish laundry"]}/>
            <List tasks = {["Mow the lawn", "walk the dog"]}/>
        </div>
    }

}

ReactDOM.render(<Main/>, document.getElementById('root'));
