    console.log(this.state)
}


    render(){ 
        return (
        <div>

          <Route exact path = "/" render={() => (
            <div>
                <Title title = {'Kenya Wall'}/>
                 <KenyaWall posts={this.state.posts} onRemovePhoto={this.removePhoto} onNavigate = {this.navigate} />
            </div>

          )}/>

          <Route path ="/addPhoto" component= {AddPhoto}/>

    </div>
        )}
}


